import React, { useState } from 'react';
import { stickers, categories, Sticker } from '../utils/stickersData';
import { Undo2, Redo2, Trash2 } from 'lucide-react';
import { motion } from 'framer-motion';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  width: 300px;
  background: #fff;
  padding: 1rem;
  border-right: 1px solid #ddd;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
`;

const Header = styled.h2`
  margin-bottom: 1rem;
`;

const CategoriesContainer = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1rem;
`;

const CategoryButton = styled.button<{ active: boolean }>`
  flex: 1;
  padding: 0.5rem;
  background: ${({ active }) => (active ? '#ddd' : '#f5f5f5')};
  border: 1px solid #ccc;
  border-radius: 6px;
  cursor: pointer;
`;

const StickerGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 0.5rem;
`;

const StickerItem = styled(motion.div)`
  background: #f0f0f0;
  border-radius: 12px;
  padding: 0.5rem;
  cursor: grab;
  perspective: 1000px;
  img {
    width: 100%;
    transform-style: preserve-3d;
  }
`;

const Toolbar = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: auto;
  padding-top: 1rem;
  border-top: 1px solid #ddd;
`;

const ToolbarButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
`;

const Sidebar: React.FC<{ onStickerDragStart: (sticker: Sticker) => void }> = ({ onStickerDragStart }) => {
  const [selectedCategory, setSelectedCategory] = useState<string>('weather');

  const handleDragStart = (event: React.DragEvent<HTMLDivElement>, sticker: Sticker) => {
    onStickerDragStart(sticker);
  };

  return (
    <SidebarContainer>
      <Header>Stickers</Header>
      <CategoriesContainer>
        {categories.map((cat) => (
          <CategoryButton
            key={cat.id}
            active={selectedCategory === cat.id}
            onClick={() => setSelectedCategory(cat.id)}
          >
            {cat.icon}
          </CategoryButton>
        ))}
      </CategoriesContainer>

      <StickerGrid>
        {stickers
          .filter((s) => s.category === selectedCategory)
          .map((sticker) => (
            <StickerItem
              key={sticker.id}
              whileHover={{ scale: 1.1, rotateY: 10 }}
              whileTap={{ scale: 0.95, rotateY: -10 }}
              drag
              dragConstraints={{ left: 0, right: 0, top: 0, bottom: 0 }}
              onDragStart={(e) => handleDragStart(e, sticker)}
            >
              <img src={sticker.image} alt={sticker.name} draggable={false} />
            </StickerItem>
          ))}
      </StickerGrid>

      <Toolbar>
        <ToolbarButton><Undo2 size={20} /></ToolbarButton>
        <ToolbarButton><Redo2 size={20} /></ToolbarButton>
        <ToolbarButton><Trash2 size={20} /></ToolbarButton>
      </Toolbar>
    </SidebarContainer>
  );
};

export default Sidebar;
