import React from 'react';
import styled from 'styled-components';
import { useMoodboard } from '../context/MoodboardContext';

const TimelineContainer = styled.div`
  grid-area: timeline;
  background-color: var(--bg-secondary);
  border-radius: 8px;
  padding: 12px;
  margin-top: 8px;
  box-shadow: 0 2px 4px var(--shadow-color);
`;

const TimelineBar = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  height: 48px;
  padding: 0 16px;
  background-color: var(--bg-tertiary);
  border-radius: 24px;
  position: relative;
`;

const TimeButton = styled.button<{ isSelected: boolean }>`
  padding: 6px 12px;
  border: none;
  border-radius: 16px;
  background-color: ${props => props.isSelected ? 'var(--bg-primary)' : 'transparent'};
  color: var(--text-primary);
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s ease;
  box-shadow: ${props => props.isSelected ? '0 2px 4px var(--shadow-color)' : 'none'};

  &:hover {
    background-color: var(--bg-primary);
    box-shadow: 0 2px 4px var(--shadow-color);
  }
`;

const Timeline: React.FC = () => {
  const { state, dispatch } = useMoodboard();
  const times = ['Morning', 'Afternoon', 'Evening', 'Night'];

  const handleTimeSelect = (time: string) => {
    dispatch({ type: 'SET_SELECTED_TIME', payload: time });
  };

  return (
    <TimelineContainer>
      <TimelineBar>
        {times.map(time => (
          <TimeButton
            key={time}
            isSelected={state.selectedTime === time}
            onClick={() => handleTimeSelect(time)}
          >
            {time}
          </TimeButton>
        ))}
      </TimelineBar>
    </TimelineContainer>
  );
};

export default Timeline;
