// Sidebar.tsx

import React, { useState } from 'react';
import { Sticker, stickers, categories } from './stickerData';

interface SidebarProps {
  onStickerSelect: (sticker: Sticker) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ onStickerSelect }) => {
  const [activeCategory, setActiveCategory] = useState<string>('weather');

  const filteredStickers = stickers.filter(
    (sticker) => sticker.category === activeCategory
  );

  return (
    <div
      style={{
        width: '280px',
        height: '100vh',
        background: '#222',
        color: 'white',
        display: 'flex',
        flexDirection: 'column',
        padding: '16px',
        boxSizing: 'border-box',
        overflowY: 'auto',
        userSelect: 'none',
      }}
    >
      <h2 style={{ marginBottom: 16 }}>Stickers</h2>

      {/* Category Tabs */}
      <div style={{ display: 'flex', marginBottom: 16, gap: 8 }}>
        {categories.map((cat) => (
          <button
            key={cat.id}
            style={{
              flex: 1,
              padding: '8px',
              background: activeCategory === cat.id ? cat.color : '#555',
              border: 'none',
              borderRadius: '6px',
              color: 'white',
              cursor: 'pointer',
              whiteSpace: 'nowrap',
            }}
            onClick={() => setActiveCategory(cat.id)}
          >
            {cat.icon} {cat.name}
          </button>
        ))}
      </div>

      {/* Stickers List */}
      <div style={{ flexGrow: 1, overflowY: 'auto', minHeight: 0 }}>
        {filteredStickers.map((sticker) => (
          <div
            key={sticker.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              marginBottom: 12,
              padding: 8,
              background: '#333',
              borderRadius: 8,
              cursor: 'grab',
            }}
            draggable
            onDragStart={(e) =>
              e.dataTransfer.setData('text/plain', sticker.id)
            }
            onClick={() => onStickerSelect(sticker)}
          >
            <img
              src={sticker.image}
              alt={sticker.name}
              width={40}
              height={40}
              style={{ marginRight: 12 }}
            />
            <span>{sticker.name}</span>
          </div>
        ))}
      </div>

      <div style={{ fontSize: 12, color: '#999', marginTop: 12 }}>
        Drag a sticker onto the 3D scene or click to add at center.
      </div>
    </div>
  );
};

export default Sidebar;
