import React, { useRef } from 'react';
import styled from 'styled-components';
import Draggable from 'react-draggable';
import { useMoodboard } from '../context/MoodboardContext';
import { Sticker } from '../utils/stickersData';

const CanvasContainer = styled.div`
  flex: 1;
  position: relative;
  background: #f5f5f5;
  overflow: hidden;
  border-radius: 8px;
  min-height: 500px;
`;

const StickerWrapper = styled.div<{ isHighlighted: boolean }>`
  position: absolute;
  cursor: move;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
  opacity: ${props => props.isHighlighted ? 1 : 0.7};
  transition: opacity 0.3s ease;
  width: 100px;
  height: 100px;
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 8px;

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const TimeLabel = styled.div`
  font-size: 0.8rem;
  color: #666;
  background: rgba(255, 255, 255, 0.9);
  padding: 2px 6px;
  border-radius: 4px;
  position: absolute;
  bottom: -20px;
  left: 50%;
  transform: translateX(-50%);
  white-space: nowrap;
`;

const Canvas: React.FC = () => {
  const { state, dispatch } = useMoodboard();
  const canvasRef = useRef<HTMLDivElement>(null);

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const stickerData = e.dataTransfer.getData('application/json');
    if (!stickerData) return;

    const sticker: Sticker = JSON.parse(stickerData);
    const rect = canvasRef.current?.getBoundingClientRect();
    if (!rect) return;

    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const newSticker = {
      ...sticker,
      id: `${sticker.id}-${Date.now()}`,
      position: { x, y },
      size: 100, // Fixed size
      rotation: 0,
      time: state.selectedTime,
      zIndex: state.stickers.length
    };

    dispatch({ type: 'ADD_STICKER', payload: newSticker });
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrag = (e: any, stickerId: string) => {
    const { x, y } = e;
    dispatch({
      type: 'UPDATE_STICKER',
      payload: {
        id: stickerId,
        updates: { position: { x, y } }
      }
    });
  };

  return (
    <CanvasContainer
      ref={canvasRef}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      {state.stickers
        .filter(sticker => sticker.time === state.selectedTime)
        .map(sticker => (
          <Draggable
            key={sticker.id}
            position={sticker.position}
            onDrag={(e, data) => handleDrag(data, sticker.id)}
            bounds="parent"
          >
            <StickerWrapper
              isHighlighted={sticker.time === state.selectedTime}
              style={{
                transform: `rotate(${sticker.rotation}deg)`,
                zIndex: sticker.zIndex
              }}
            >
              <img 
                src={sticker.image} 
                alt={sticker.name}
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.src = 'https://via.placeholder.com/100?text=No+Image';
                }}
              />
              <TimeLabel>{sticker.time}</TimeLabel>
            </StickerWrapper>
          </Draggable>
        ))}
    </CanvasContainer>
  );
};

export default Canvas;
