import React from 'react';
import styled from 'styled-components';
import { useMoodboard } from '../context/MoodboardContext';
import { stickers } from '../utils/stickersData';

const SidebarContainer = styled.div`
  width: 250px;
  background: #ffffff;
  border-right: 1px solid #e0e0e0;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const StickerGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 10px;
  padding: 10px;
`;

const StickerItem = styled.div`
  aspect-ratio: 1;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  padding: 5px;
  cursor: grab;
  transition: transform 0.2s, box-shadow 0.2s;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const CategoryTitle = styled.h3`
  font-size: 1.1rem;
  color: #333;
  margin: 0;
  padding: 10px;
  background: #f5f5f5;
  border-radius: 4px;
`;

const Sidebar: React.FC = () => {
  const { state, dispatch } = useMoodboard();

  const handleDragStart = (e: React.DragEvent, sticker: typeof stickers[0]) => {
    e.dataTransfer.setData('application/json', JSON.stringify(sticker));
    e.dataTransfer.effectAllowed = 'copy';
  };

  const handleDeleteSelected = () => {
    if (state.selectedSticker) {
      dispatch({ type: 'DELETE_STICKER', payload: state.selectedSticker });
    }
  };

  const handleUndo = () => {
    dispatch({ type: 'UNDO' });
  };

  const handleRedo = () => {
    dispatch({ type: 'REDO' });
  };

  const groupedStickers = stickers.reduce((acc, sticker) => {
    if (!acc[sticker.category]) {
      acc[sticker.category] = [];
    }
    acc[sticker.category].push(sticker);
    return acc;
  }, {} as Record<string, typeof stickers>);

  return (
    <SidebarContainer>
      <div>
        <h2>Stickers</h2>
        {Object.entries(groupedStickers).map(([category, categoryStickers]) => (
          <div key={category}>
            <CategoryTitle>{category}</CategoryTitle>
            <StickerGrid>
              {categoryStickers.map(sticker => (
                <StickerItem
                  key={sticker.id}
                  draggable
                  onDragStart={e => handleDragStart(e, sticker)}
                >
                  <img src={sticker.image} alt={sticker.name} />
                </StickerItem>
              ))}
            </StickerGrid>
          </div>
        ))}
      </div>
      <div>
        <h3>Actions</h3>
        <button onClick={handleUndo} disabled={state.historyIndex === 0}>
          Undo
        </button>
        <button onClick={handleRedo} disabled={state.historyIndex === state.history.length - 1}>
          Redo
        </button>
        <button onClick={handleDeleteSelected} disabled={!state.selectedSticker}>
          Delete Selected
        </button>
      </div>
    </SidebarContainer>
  );
};

export default Sidebar;
