import React, { useRef, useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import Draggable from 'react-draggable';
import { useMoodboard } from '../context/MoodboardContext';
import { Sticker } from '../utils/stickersData';

const float = keyframes`
  0% { transform: translateY(0px) rotate(0deg) scale(1); }
  50% { transform: translateY(-20px) rotate(5deg) scale(1.05); }
  100% { transform: translateY(0px) rotate(0deg) scale(1); }
`;

const pulse = keyframes`
  0% { opacity: 0.5; }
  50% { opacity: 0.8; }
  100% { opacity: 0.5; }
`;

const CanvasContainer = styled.div`
  flex: 1;
  position: relative;
  background-color: var(--bg-primary);
  overflow: hidden;
  border-radius: 8px;
  min-height: 0; // Allow container to shrink
  display: flex;
  flex-direction: column;
`;

const CanvasArea = styled.div`
  flex: 1;
  position: relative;
  overflow: hidden;
  border-radius: 8px;
  background: linear-gradient(135deg, var(--bg-secondary) 0%, var(--bg-tertiary) 100%);
`;

const BackgroundBox = styled.div<{ size: number; delay: number; x: number; y: number }>`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: var(--box-bg);
  border: 2px solid var(--box-border);
  border-radius: 8px;
  left: ${props => props.x}%;
  top: ${props => props.y}%;
  animation: 
    ${float} ${props => 3 + props.delay}s ease-in-out infinite,
    ${pulse} ${props => 4 + props.delay}s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
  backdrop-filter: blur(4px);
  box-shadow: 0 4px 12px var(--shadow-color);
  transform-style: preserve-3d;
  perspective: 1000px;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.1) rotate(10deg);
    box-shadow: 0 8px 24px var(--shadow-color);
  }
`;

const StickerWrapper = styled.div<{ isHighlighted: boolean }>`
  position: absolute;
  cursor: move;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
  opacity: ${props => props.isHighlighted ? 1 : 0.7};
  transition: all 0.3s ease;
  width: 100px;
  height: 100px;
  background: var(--sticker-bg);
  border-radius: 12px;
  box-shadow: 0 4px 12px var(--shadow-color);
  padding: 12px;
  transform-origin: center center;
  backdrop-filter: blur(8px);

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 6px 16px var(--shadow-color);
  }

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    filter: drop-shadow(0 2px 4px var(--shadow-color));
  }
`;

const TimeLabel = styled.div`
  font-size: 0.8rem;
  color: var(--text-secondary);
  background: var(--sticker-bg);
  padding: 4px 8px;
  border-radius: 6px;
  position: absolute;
  bottom: -24px;
  left: 50%;
  transform: translateX(-50%);
  white-space: nowrap;
  box-shadow: 0 2px 4px var(--shadow-color);
  font-weight: 500;
  backdrop-filter: blur(4px);
`;

const LoadingSpinner = styled.div`
  width: 40px;
  height: 40px;
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin: auto;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Canvas: React.FC = () => {
  const { state, dispatch } = useMoodboard();
  const canvasRef = useRef<HTMLDivElement>(null);
  const [loadingImages, setLoadingImages] = useState<{ [key: string]: boolean }>({});
  const [backgroundBoxes, setBackgroundBoxes] = useState<Array<{ size: number; delay: number; x: number; y: number }>>([]);

  useEffect(() => {
    // Generate random background boxes with more variety
    const boxes = Array.from({ length: 20 }, () => ({
      size: Math.random() * 80 + 40, // Larger size range
      delay: Math.random() * 3, // More varied delays
      x: Math.random() * 100,
      y: Math.random() * 100
    }));
    setBackgroundBoxes(boxes);
  }, []);

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const stickerData = e.dataTransfer.getData('application/json');
    if (!stickerData) return;

    const sticker: Sticker = JSON.parse(stickerData);
    const rect = canvasRef.current?.getBoundingClientRect();
    if (!rect) return;

    const x = e.clientX - rect.left - 50; // Center the sticker
    const y = e.clientY - rect.top - 50;  // Center the sticker

    const newSticker = {
      ...sticker,
      id: `${sticker.id}-${Date.now()}`,
      position: { x, y },
      size: 100, // Slightly smaller size
      rotation: 0,
      time: state.selectedTime,
      zIndex: state.stickers.length
    };

    dispatch({ type: 'ADD_STICKER', payload: newSticker });
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrag = (e: any, stickerId: string) => {
    const { x, y } = e;
    dispatch({
      type: 'UPDATE_STICKER',
      payload: {
        id: stickerId,
        updates: { position: { x, y } }
      }
    });
  };

  const handleImageLoad = (stickerId: string) => {
    setLoadingImages(prev => ({ ...prev, [stickerId]: false }));
  };

  const handleImageError = (e: React.SyntheticEvent<HTMLImageElement, Event>, stickerId: string) => {
    const target = e.target as HTMLImageElement;
    target.src = 'https://raw.githubusercontent.com/twitter/twemoji/master/assets/72x72/2753.png';
    setLoadingImages(prev => ({ ...prev, [stickerId]: false }));
  };

  return (
    <CanvasContainer>
      <CanvasArea
        ref={canvasRef}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        {backgroundBoxes.map((box, index) => (
          <BackgroundBox
            key={index}
            size={box.size}
            delay={box.delay}
            x={box.x}
            y={box.y}
          />
        ))}
        {state.stickers
          .filter(sticker => sticker.time === state.selectedTime)
          .map(sticker => (
            <Draggable
              key={sticker.id}
              position={sticker.position}
              onDrag={(e, data) => handleDrag(data, sticker.id)}
              bounds="parent"
            >
              <StickerWrapper
                isHighlighted={sticker.time === state.selectedTime}
                style={{
                  transform: `rotate(${sticker.rotation}deg)`,
                  zIndex: sticker.zIndex
                }}
              >
                {loadingImages[sticker.id] && <LoadingSpinner />}
                <img 
                  src={sticker.image} 
                  alt={sticker.name}
                  onLoad={() => handleImageLoad(sticker.id)}
                  onError={(e) => handleImageError(e, sticker.id)}
                  style={{ display: loadingImages[sticker.id] ? 'none' : 'block' }}
                />
                <TimeLabel>{sticker.time}</TimeLabel>
              </StickerWrapper>
            </Draggable>
          ))}
      </CanvasArea>
    </CanvasContainer>
  );
};

export default Canvas;
