import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useMoodboard } from '../context/MoodboardContext';
import { stickers, categories } from '../utils/stickersData';
import { Undo2, Redo2, Trash2 } from 'lucide-react';

const SidebarContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 260px;
  background-color: var(--bg-primary);
  border-right: 1px solid var(--border-color);
  overflow-y: auto;
`;

const CategorySection = styled.div`
  margin-bottom: 20px;
`;

const CategoryTitle = styled.h3`
  font-size: 1rem;
  padding: 12px 16px 8px;
  margin: 0;
  color: var(--text-secondary);
  display: flex;
  align-items: center;
  gap: 8px;
`;

const StickersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 12px;
  padding: 0 16px;
`;

const StickerItem = styled(motion.div)`
  background: var(--sticker-bg);
  border-radius: 12px;
  padding: 10px;
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: grab;
  box-shadow: 0 3px 8px rgba(0, 0, 0, 0.15);
  transition: transform 0.2s;
  backface-visibility: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    pointer-events: none;
  }
`;

const ActionsContainer = styled.div`
  padding: 16px;
  border-top: 1px solid var(--border-color);
  background-color: var(--bg-primary);
  display: flex;
  gap: 8px;
  justify-content: space-around;
`;

const ActionButton = styled.button`
  background-color: var(--bg-secondary);
  border: none;
  padding: 8px 12px;
  border-radius: 8px;
  cursor: pointer;
  color: var(--text-primary);
  display: flex;
  align-items: center;
  gap: 4px;
  transition: background 0.2s ease;

  &:hover {
    background-color: var(--bg-tertiary);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const Sidebar: React.FC = () => {
  const { state, dispatch } = useMoodboard();

  const handleDragStart = (e: React.DragEvent, sticker: any) => {
    e.dataTransfer.setData('application/json', JSON.stringify(sticker));
  };

  const handleDeleteSelected = () => {
    if (state.selectedSticker) {
      dispatch({ type: 'DELETE_STICKER', payload: state.selectedSticker });
    }
  };

  const handleUndo = () => dispatch({ type: 'UNDO' });
  const handleRedo = () => dispatch({ type: 'REDO' });

  return (
    <SidebarContainer>
      {categories.map(category => (
        <CategorySection key={category.id}>
          <CategoryTitle>
            <span>{category.icon}</span>
            {category.name}
          </CategoryTitle>
          <StickersGrid>
            {stickers
              .filter(s => s.category === category.id)
              .map(sticker => (
                <StickerItem
                  key={sticker.id}
                  whileHover={{ scale: 1.08, rotate: 2 }}
                  whileTap={{ scale: 0.95 }}
                  draggable
                  onDragStart={e => handleDragStart(e, sticker)}
                >
                  <img src={sticker.image} alt={sticker.name} loading="lazy" />
                </StickerItem>
              ))}
          </StickersGrid>
        </CategorySection>
      ))}

      <ActionsContainer>
        <ActionButton onClick={handleUndo} disabled={state.historyIndex === 0}>
          <Undo2 size={16} /> Undo
        </ActionButton>
        <ActionButton
          onClick={handleRedo}
          disabled={state.historyIndex === state.history.length - 1}
        >
          <Redo2 size={16} /> Redo
        </ActionButton>
        <ActionButton onClick={handleDeleteSelected} disabled={!state.selectedSticker}>
          <Trash2 size={16} /> Delete
        </ActionButton>
      </ActionsContainer>
    </SidebarContainer>
  );
};

export default Sidebar;
