import React from 'react';
import styled from 'styled-components';
import { useMoodboard } from '../context/MoodboardContext';

const TimelineContainer = styled.div`
  height: 100px;
  background: #ffffff;
  border-top: 1px solid #e0e0e0;
  padding: 10px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const TimeMarkers = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0 20px;
`;

const TimeMarker = styled.div<{ isSelected: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5px;
  cursor: pointer;
  padding: 5px;
  border-radius: 4px;
  background: ${props => props.isSelected ? '#e3f2fd' : 'transparent'};
  transition: background-color 0.2s;

  &:hover {
    background: #f5f5f5;
  }
`;

const TimeLabel = styled.span`
  font-size: 0.8rem;
  color: #666;
`;

const StickerPreview = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 4px;
  overflow: hidden;
  background: #f5f5f5;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.7rem;
`;

const TimelineBar = styled.div`
  height: 4px;
  background: #e0e0e0;
  border-radius: 2px;
  position: relative;
  margin: 0 20px;
`;

const TimelineProgress = styled.div<{ progress: number }>`
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  width: ${props => props.progress}%;
  background: #2196f3;
  border-radius: 2px;
  transition: width 0.3s ease;
`;

const Timeline: React.FC = () => {
  const { state, dispatch } = useMoodboard();
  const times = ['00:00', '03:00', '06:00', '09:00', '12:00', '15:00', '18:00', '21:00'];

  const handleTimeClick = (time: string) => {
    dispatch({ type: 'SET_TIME', payload: time });
  };

  const getStickerCount = (time: string) => {
    return state.stickers.filter(sticker => sticker.time === time).length;
  };

  const getProgress = () => {
    const totalStickers = state.stickers.length;
    if (totalStickers === 0) return 0;

    const timeIndex = times.indexOf(state.selectedTime);
    return ((timeIndex + 1) / times.length) * 100;
  };

  return (
    <TimelineContainer>
      <TimelineBar>
        <TimelineProgress progress={getProgress()} />
      </TimelineBar>
      <TimeMarkers>
        {times.map(time => (
          <TimeMarker
            key={time}
            isSelected={time === state.selectedTime}
            onClick={() => handleTimeClick(time)}
          >
            <TimeLabel>{time}</TimeLabel>
            <StickerPreview>
              {getStickerCount(time)}
            </StickerPreview>
          </TimeMarker>
        ))}
      </TimeMarkers>
    </TimelineContainer>
  );
};

export default Timeline;
